
public class switchStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//which student will do assembly 
		
		String week_day= "Monday";
				
				switch (week_day) {
				case "Monday":
					System.out.println("Student 1 will do assembly");
					break;
				case "Tuesday":
					System.out.println("Student 2 will do assembly");
					break;
				case "Wednesday":
					System.out.println("Student 3 will do assembly");
					break;
				case "Thursday":
					System.out.println("Student 4 will do assembly");
					break;
				case "Friday":
					System.out.println("Student 5 will do assembly");
					break;
				case "Satuday":
					System.out.println("Student 6 will do assembly");
					break;
				default:
					System.out.println("Only student 1-7 can do assembly");
					break;
					
				}
				}
		
		

	}


