
public class WhileLoopStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numberCount=100;
		
		while(numberCount>=0) {
			if (numberCount%3==0 && numberCount%5==0) 
				
				System.out.println("Number is divisible by 3 and 5: "+ numberCount);
			
				numberCount--;
			}
			
		}
		}
		

